package Entitie;import Enum.ComponentType;public abstract class Component {    protected int id;    protected String name;    protected ComponentType type;    protected Double vatRate;    protected Project project;    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public ComponentType getType() {        return type;    }    public void setType(ComponentType type) {        this.type = type;    }    public Double getvatRate() {        return vatRate;    }    public void setvatRate(Double vatRate) {        this.vatRate = vatRate;    }    public abstract double calculateTotalCost();    public abstract double calculateVatRate();}interface Components {    double calculateTotalCost();    double calculateVatRate();}